for (const item of items) {
  const data = item.json;

  console.log('Response status:', data.status);
  console.log('Has result:', !!data.result);

  let reservations = [];
  if (data.status === "success" && data.result && Array.isArray(data.result)) {
    reservations = data.result;
  }

  console.log('Reservations found:', reservations.length);

  for (const reservation of reservations) {
    const createdDate = new Date(reservation.insertedOn || reservation.createdAt);
    const oneDayAgo = new Date(Date.now() - 24 * 60 * 60 * 1000);

    let eventType = 'new';
    let changeDetails = '';

    if (createdDate < oneDayAgo) {
      const criticalChanges = [];

      if (reservation.status === 'cancelled') {
        criticalChanges.push('Cancelled');
      }

      const checkIn = new Date(reservation.arrivalDate);
      if (checkIn <= new Date(Date.now() + 7 * 24 * 60 * 60 * 1000)) {
        criticalChanges.push('Dates modified');
      }

      if (reservation.paymentStatus && reservation.paymentStatus !== 'Paid') {
        criticalChanges.push(`Payment: ${reservation.paymentStatus}`);
      }

      if (criticalChanges.length > 0) {
        eventType = 'updated';
        changeDetails = ` (${criticalChanges.join(', ')})`;
      } else {
        continue;
      }
    }

    const listing = listingCodes[reservation.listingMapId];
    const listingDisplay = listing ? `${listing.flag} ${listing.code}` : `Property ${reservation.listingMapId}`;

    const event = {
      event_type: eventType,
      change_details: changeDetails,
      urgency: eventType === 'updated' ? 'high' : 'normal',
      reservation: {
        id: reservation.id,
        listingName: listingDisplay,
        checkIn: reservation.arrivalDate,
        checkOut: reservation.departureDate,
        guests: reservation.numberOfGuests || 1,
        total: reservation.totalPrice || 0,
        currency: reservation.currency || 'USD',
        channel: reservation.channelName || 'Direct',
        status: reservation.status,
        guestName: reservation.guestName || 'N/A'
      }
    };

    events.push(event);
  }
}

